import sysanswer = []nameFile = open('Sort Me.txt')#check args for "-r" to sort in reverseargList = str(sys.argv)if "-r" in argList:	reverse = bool(True)else:	reverse = bool(False)for name in nameFile:	#remove extra spaces, add to answer array	name = name.strip()	answer.append(name)		#sort the names by length, then alphabetically (reverse if -r arg added)if reverse == False:	answer.sort()	answer.sort(key=len)else:	answer.sort(reverse=True)	answer.sort(key=len, reverse=True)	#print the resultsfor word in answer:	print(word)